t.script:
function openPopup(event, url) {
    event.preventDefault();
    var windowFeatures = 'width=800,height=600,scrollbars=yes,resizable=yes,location=no,toolbar=no';
    var popupWindow = window.open(url, '_blank', windowFeatures);
    if (popupWindow) {
        popupWindow.focus();
    } else {
        alert('No se pudo abrir el popup. Asegúrate de que los popups no estén bloqueados.');
    }
}
